import React from "react";
import { useState, useEffect } from "react";
import "./Movie.css";

export default function Movies() {
  const [movies, setMovies] = useState([]);

  const movieRatingClass = () => {
    movie.rating >= 9 ? 'good' : movie.rating >= 7 ? 'soso' : 'bad'  
  }


  // mounted
  useEffect(() => {
    fetch("https://yts.mx/api/v2/list_movies.json")
    .then((res) => res.json())
    .then((json) => { 
      setMovies(json.data.movies);
     });
  },[]);

  console.log("movies: ", movies);

  const render = movies.map(movie => {
    return (
      <div key={movie.id}>
        <a className="movieTitle" href={movie.url}>
          {movie.title}({movie.year})
        </a>
        <div>
          評価:
          <span className={movieRatingClass}>{movie.rating}</span>/10点
        </div>
        <div>ジャンル:{movie.gernes.join(", ")}</div>
        <div>ランタイム:{movie.runtime}分</div>
        <div>ストーリー:{movie.synopsis || "情報なし"}</div>
        <img 
          clasName="movieImg" 
          src={movie.large_cover_image} 
          alt={movie.title}></img>
      </div>
    );
  });

  return (
  <div>
    <h1>Movies</h1>
    <div>{render}</div>
  </div>
  );
}
