import "./Movie.css";
import React from "react";
import Box from "@mui/material/Box";
import Rating from "@mui/material/Rating";
import CircularProgress from "@mui/material/CircularProgress";
import Tooltip from '@mui/material/Tooltip';
import { useState, useEffect } from "react";

export default function Movies() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const movieRatingClass = (rating) => {
    return rating >= 9 ? "good" : rating >= 7 ? "soso" : "bad";
  };

  // mounted
  useEffect(() => {
    // Loading中のフラグ
    setIsLoading(true);
    //fetch("https://yts.mx/api/v2/list_movies.json?sort_by=rating")
    fetch("https://yts.mx/api/v2/list_movies.json")
      .then((res) => res.json())
      .then((json) => {
        setIsLoading(false);
        setMovies(json.data.movies);
      });
  }, []);

  console.log("movies: ", movies);

  const loading = (
    <Box sx={{ display: "flex" }}>
      <CircularProgress />
    </Box>
  );

  const render = movies.map((movie) => {
    return (
      <div key={movie.id}>
        <a className="movieTitle" href={movie.url}>
          {movie.title}({movie.year})
        </a>
        <div>
          評価:
          <span className={movieRatingClass(movie.rating)}>{movie.rating}</span>
          /10点




          <Box
            sx={{
              "& > legend": { mt: 2 },
            }}
          >
            <Rating name="simple-controlled" readOnly value={movie.rating / 2} />
          </Box>
        </div>
        <div>ジャンル:{movie.gernes}</div>
        <div>ランタイム:{movie.runtime}分</div>
        <div>ストーリー:{movie.synopsis || "情報なし"}</div>
        <img
          clasName="movieImg"
          src={movie.large_cover_image}
          alt={movie.title}
        ></img>
      </div>
    );
  });

  return (
    <div>
      <h1>Movies</h1>
      <div>{isLoading ? loading : render}</div>
    </div>
  );
}
