export default function Todos() {
  const { todos, addTodo, delTodo, toggleTodo } = useTodos(); // useTodosのメソッドを展開

  const [inputText, setInputText] = useState("");

  // Mounted
  // useEffect(() => {
  //   fetch("https://jsonplaceholder.typicode.com/todos")
  //     .then((res) => res.json())
  //     .then((json) => {
  //       const result = json.filter((item) => item.useId === 1);
  //       setTodos(result);
  //       setTodoKey(result.length + 1);
  //     });
  // }, []);

  const render = todos.map((todo) => {
    return (
      <div key={todo.id}>
        <div>
          <span
            className={
              todo.completed ? "text-gray-500 line-through" : "font-bold"
            }
            onClick={() => toggleTodo(todo.id)} // 関数として渡す
          >
            #{todo.id} / {todo.title}
          </span>
          <Checkbox
            checked={todo.completed}
            onChange={() => toggleTodo(todo.id)} // 関数として渡す
          />
          <IconButton aria-label="delete" onClick={() => delTodo(todo.id)}> {/* 関数として渡す */}
            <DeleteIcon />
          </IconButton>
        </div>
      </div>
    );
  });

  const handleAddTodo = () => {
    addTodo(inputText); // 関数呼び出し
    setInputText("");
  };

  return (
    <div>
      <Label text="Todos" />

      <div className="mb-[20px]">
        <span className="mr-[5px]">
          <TextField
            value={inputText}
            size="small"
            required
            placeholder="New Todo"
            onChange={(e) => {
              setInputText(e.target.value);
            }}
          ></TextField>
        </span>
        <NormalButton buttonText="SEND" onClick={handleAddTodo} /> {/* 関数として渡す */}
      </div>
      <div>{render}</div>
    </div>
  );
}

