import React from "react";
import { useStat, useEffect } from "react";
import "./News.css";

export default function News() {
  const [news, setNews] = useState([]);

  const movieRatingClass = (rating) => {
    return rating >= 9 ? "good" : rating >= 7 ? "soso" : "bad";
  };

  // mounted
  useEffect(() => {
    //fetch("https://yts.mx/api/v2/list_movies.json?sort_by=rating")
    fetch("https://yts.mx/api/v2/list_news.json")
      .then((res) => res.json())
      .then((json) => {
        setMovies(json.data.news);
      });
  }, []);

  console.log("news: ", news);

  const render = news.map((news) => {
    return (
      <div key={news.id}>
        <a className="newsTitle" href={movie.url}>
          {movie.title}({movie.year})
        </a>
        <div>
          評価:
          <span className={movieRatingClass(movie.rating)}>{movie.rating}</span>
          /10点
        </div>
        <div>ジャンル:{movie.gernes.join(", ")}</div>
        <div>ランタイム:{movie.runtime}分</div>
        <div>ストーリー:{movie.synopsis || "情報なし"}</div>
        <img
          clasName="movieImg"
          src={movie.large_cover_image}
          alt={movie.title}
        ></img>
      </div>
    );
  });

  return (
    <div>
      <h1>Movies</h1>
      <div>{render}</div>
    </div>
  );
}
