import "./App.css";
import { useState } from "react";


export default function App() {
  const [count, setCount] =useState(0);


  const increase = () => {
    setCount(count + 1);
  };

  const decrease = () => {
    setCount(count - 1);
    count = count - 1;
  };

  return ( 
    <div className="App">
      <div>count : {count}</div>
      <button onClick={increase}>+</button>
      <button onClick={decrease}>-</button>
    </div>
  );
}
