import { useState, useEffect } from "react";


export default function Count() {
  //let count = 0の代わりに
  const [count, setCount] =useState(0);


  const increase = () => {
    setCount(count + 1);
  };

  const decrease = () => {
    setCount(count - 1);
  };

  // mounted
  useEffect(() => {
    console.log("mounted");
  }, []);

  //unmounted
  useEffect(() => {
    return () => {
      console.log("unmounted");
    };
  }, []);

  return ( 
    <div className="App">
      <div>count : {count}</div>
      <button onClick={increase}>+</button>
      <button onClick={decrease}>-</button>
    </div>
  );
}
