import "./App.css";


export default function App() {
  const count = 0;

  const increase = () => {
    count = count + 1;
  };

  const decrease = () => {
    count = count - 1;
  };

  return ( 
    <div className="App">
      <div>count : {count}</div>
      <button>+</button>
      <button>-</button>
    </div>
  );
}
